import React, { useState } from 'react'
import { Calendar, Views, momentLocalizer } from 'react-big-calendar'
import moment from 'moment'
import events from './events'
import * as dates from '../node_modules/react-big-calendar/lib/utils/dates'
import 'react-big-calendar/lib/css/react-big-calendar.css'
import EventModal from './EventModal'
import { Button, Modal, ModalHeader, ModalBody, ModalFooter, Form, FormGroup, Label, Input } from 'reactstrap';

let allViews = Object.keys(Views).map(k => Views[k])

const localizer = momentLocalizer(moment)

const ColoredDateCellWrapper = ({ children }) =>
  React.cloneElement(React.Children.only(children), {
    style: {
      backgroundColor: 'lightblue',
    },
  })


let MyCalendar = () => {
  // const [modal] = useState(true);
  const [event, setevent] = useState(events)
  const [modal, setModal] = useState(false);
  const toggle = () => setModal(!modal);
  const [name, setName]=''

  // const eventModal = () => {

  //   debugger
  //   return (alert('hello'))
  // }
  const handleChange=(e)=>{
    setName(e.target.value)
  }
  const handleSelect = ({ start, end }) => {
    debugger
    toggle()
    // const title = window.prompt('New Event Name')
    if (inputValues)
      setevent([
          ...event,
          {
            start,
            end,

          },
        ],
      )
  }
  return (
    <div>
      <Calendar
        events={event}
        views={allViews}
        step={60}
        showMultiDayTimes
        max={dates.add(dates.endOf(new Date(2020, 3, 1), 'day'), -1, 'hours')}
        defaultDate={new Date(2020, 3, 1)}
        components={{
          timeSlotWrapper: ColoredDateCellWrapper,
        }}
        localizer={localizer}
        style={{ height: 500 }}

        selectable
        onSelectEvent={event => alert(event.title)}
        onSelectSlot={handleSelect}
      />
      <Modal isOpen={modal} toggle={toggle} className="a">
        <ModalHeader toggle={toggle}>Modal title</ModalHeader>
        <ModalBody>
          <Form>
            <FormGroup>
              <Label for="eventName">Name</Label>
              <Input type="text" name="name" id="eventName"  onChange={handleChange}/>
            </FormGroup>
            <FormGroup>
              <Label for="eventStart">Start</Label>
              <Input type="date" name="start" id="eventStart" />
            </FormGroup>
            <FormGroup>
              <Label for="eventEnd">End</Label>
              <Input type="date" name="end" id="eventEnd" />
            </FormGroup>
          </Form>
        </ModalBody>
        <ModalFooter>
          <Button color="primary" onClick={toggle}>Do Something</Button>{' '}
          <Button color="secondary" onClick={toggle}>Cancel</Button>
        </ModalFooter>
      </Modal>
    </div>
  )
}



export default MyCalendar;
