import React, { Component } from 'react'
import { Calendar, momentLocalizer, Views } from 'react-big-calendar'
import moment from 'moment'
import 'react-big-calendar/lib/css/react-big-calendar.css';

const localizer = momentLocalizer(moment)

export class MyCalendar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      eventObj: {}
    };
  }

  handleSelect = ({start, end}) => {
    const title = window.prompt('Enter Event name')
    if (title) {
      // console.log(title)
      this.setState({
        eventObj: [
          ...this.state.event,

          {
            start,
            end,
            title
          }
        ]
      })
      // const eventData = {
      //   'title': title,
      //   'allDay': true,
      //   'start': '',
      //   'end': '',
      //   'action': ''
      // }
      // this.setState({
      //   eventObj: eventData
      // })
    }

  }
  ColoredDateCellWrapper = ({ children }) => {
    React.cloneElement(React.Children.only(children), {
      style: {
        backgroundColor: 'lightblue',
      },
    })
  }
  render() {
    const allViews = Object.keys(Views).map(k => Views[k])
    const { localizer } = this.props
    return (
      <div>
        <Calendar
          selectable
          localizer={localizer}
          events={[
            {
              'title': 'Long Event',
              'start': new Date(2020, 4, 27),
              'end': new Date(2020, 4, 28)
            }
          ]}
          titleAccessor=""
          views={allViews}
          startAccessor="start"
          endAccessor="end"
          style={{ height: 500 }}
          onSelectSlot={this.handleSelect}

          showMultiDayTimes
          components={{
            timeSlotWrapper: this.ColoredDateCellWrapper,
          }}
        />
      </div>
    )
  }
}


export default MyCalendar;
