import React, { Component } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faLock,faUser} from '@fortawesome/free-solid-svg-icons';

import './User.css'

export default class User extends Component {
    state={
        isPasswordShown:true,
        password:''
    }
    toggleVisibility=()=>{
        const {isPasswordShown}=this.state
        this.setState({isPasswordShown:!this.state.isPasswordShown})
    }
    password=(e)=>{
        this.setState({password:e.target.value})
    }
    render() {
        const center = {
            textAlign: "center",
            color: "#fff",
            fontSize: "25px"
        }
        const pos = {
            position: "relative"
        }
        return (
            <div className="mainBg">
                <img src={process.env.PUBLIC_URL + '/nividata_white-1.png'} style={{width:'165px', marginTop:'20px', marginLeft: '40px'}}/>
                <div className="formContainer">
                    <form>
                        <h3 style={center}>Sign In</h3>
                        {/* <label className="formLabel">User Id</label><br></br> */}
                        <div style={pos}>
                            <span className="icon">
                            <FontAwesomeIcon icon={faUser} className="icon-user"/>
                            </span>
                            <input className="inputField paddingLeft" type="text" placeholder="Username" autoComplete="off"></input>
                        </div>
                        <br />
                        {/* <label className="formLabel">Password</label><br></br> */}
                       <div style={pos}>
                       <span className="icon1">
                                <FontAwesomeIcon icon={faLock} className="icon-user" onClick={this.toggleVisibility}/>
                            </span>
                        <input className="inputField" type="password" placeholder="Password" value={this.state.password} onChange={this.password}></input>
                       </div>
                        <br />
                        <input className="btn" type="submit" value="Login"></input>
                    </form>
                    <p className="register">New User?<a href="">  Register</a></p>
                </div>
            </div>
        )
    }
}
