import {
    CardBody,
    Card,
    Row,
    Col
} from 'reactstrap';
import data from './employee_database.json'
import axios from 'axios'
import './Employee.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch } from '@fortawesome/free-solid-svg-icons';



import React, { Component } from 'react'

export default class Employee extends Component {
    constructor(props) {
        super(props);
        this.state = {
            Users: [],
            initial: '',
            firstname: '',
            lastname: ''
        };
    }

    getUsersData() {
        axios
            .get('src/employee_database.json')
            .then(res => {
                const data = res.data


                const users = data.map(u =>

                    < Col lg='3' key={data.id} >
                        <div className='employee-detail text-center '>

                            <CardBody className='text-center'>
                                {data.url ? <img src={process.env.PUBLIC_URL + data.url} alt={data.name} className='rounded-circle img-fluid'></img> : <p className='initials rounded-circle'><b>{this.state.initial}</b></p>}
                                <p className='my-1'>{data.name}</p>
                                <p className='mb-0' style={{ fontSize: 14 }}>{data.jobTitle}</p>
                            </CardBody>

                        </div>
                    </Col >
                )

                this.setState({
                    users
                })

            })
            .catch((error) => {
                console.log(error)
            })

    }
    componentDidMount() {
        this.getUsersData()
    }


    render() {
        return (
            <div>
                <Card className='top-bar'>
                    <CardBody>
                        <div className='d-flex justify-content-between'>
                            <h5>Employee directory</h5>
                            <span>
                                <input type='search' name='search' className='search' ></input>
                                <FontAwesomeIcon icon={faSearch} className='search-icon'></FontAwesomeIcon>
                            </span>
                        </div>
                    </CardBody>
                </Card>
                <Card className='employee-directory'>
                    <CardBody>
                        <Row className='justufy-content-between'>
                            {this.state.users}
                        </Row>
                    </CardBody>
                </Card>
            </div>
        )
    }
}

