import React, { Component } from 'react'
import { Calendar, momentLocalizer } from 'react-big-calendar'
import moment from 'moment'
import 'react-big-calendar/lib/css/react-big-calendar.css';

const localizer = momentLocalizer(moment)

export class MyCalendar extends Component {
  constructor(props) {
    super(props);
    this.state = {
        eventObj: {}
    };
}

  state = {}
  handleSelect = (e) => {
    console.log(e)
    const title = window.prompt('New Event name')
    if (title) {
     // console.log(title)
      const eventData = {
        'title': title,
        'allDay': true,
        'start': '',
        'end': '',
        'action': '',


      }
      this.setState({
        eventObj: eventData
      })
    }

  }
  ColoredDateCellWrapper = ({ children }) =>{
    debugger
    console.log(React.cloneElement(React.Children.only(children)))
    // React.cloneElement(React.Children.only(children), {
    //   style: {
    //     backgroundColor: 'lightblue',
    //   },
    // })
  }

  render() {
    return (
      <div>
        <Calendar
          selectable
          localizer={localizer}
          events={[
            {
              'title': 'Long Event',
              'start': new Date(2020, 4, 7),
              'end': new Date(2020, 4, 10)
            }
          ]}
          startAccessor="start"
          endAccessor="end"
          style={{ height: 500 }}
          onSelectSlot={this.handleSelect}

          showMultiDayTimes
          components={{
            timeSlotWrapper: this.ColoredDateCellWrapper,
          }}
        />
      </div>
    )
  }
}


export default MyCalendar;
