import React, { Component } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faLock, faUser, faUnlock, faPassport } from '@fortawesome/free-solid-svg-icons';
import { Redirect } from 'react-router-dom'
import './User.css'

export default class User extends Component {
    constructor(props) {
        let loggenIn = 'false';
        state = {
            type: "password",
            icon: faLock,
            username: '',
            password: '',
            loggenIn: ''
        }
    }
    toggleVisibility = () => {
        if (this.state.type == "password") {
            this.setState({ type: "text", icon: faUnlock })

        } else {
            this.setState({ type: "password", icon: faLock })
        }
    }
    handleChange = (e) => {
        this.setState({ [e.target.name]: e.target.value })
    }
    submitForm = (e) => {
        e.preventDefault()
        const { username, password } = this.state;
        if (!username == '' && !password == '') {
            loggenIn: 'true'
        }
    }
    render() {
        if (this.state.loggenIn) {
            <Redirect to='./dashboard.js' />
        }
        const center = {
            textAlign: "center",
            color: "#fff",
            fontSize: "25px"
        }
        const pos = {
            position: "relative"
        }
        return (
            <div className="mainBg">
                <img src={process.env.PUBLIC_URL + '/nividata_white-1.png'} style={{ width: '165px', marginTop: '20px', marginLeft: '40px' }} />
                <div className="formContainer">
                    <form onSubmit={this.submitForm}>
                        <h3 style={center}>Sign In</h3>
                        {/* <label className="formLabel">User Id</label><br></br> */}
                        <div style={pos}>
                            <span className="icon">
                                <FontAwesomeIcon icon={faUser} className="icon-user" />
                            </span>
                            <input className="inputField paddingLeft" type="text" name='username' value={this.state.username} placeholder="Username" autoComplete="off" onChange={this.handleChange}></input>
                        </div>
                        <br />
                        {/* <label className="formLabel">Password</label><br></br> */}
                        <div style={pos}>
                            <span className="icon1">
                                <FontAwesomeIcon icon={this.state.icon} className="icon-user" onClick={this.toggleVisibility} />
                            </span>
                            <input className="inputField" type={this.state.type} name='password' value={this.state.password} placeholder="Password"></input>
                        </div>
                        <br />
                        <input className="btn" type="submit" value="Login"></input>
                    </form>
                    <p className="register">New User?<a href="">  Register</a></p>
                </div>
            </div>
        )
    }
}
