              'start': new Date(2020, 4, 19), // 10.00 AM
              'end': new Date(2020, 4, 19), // 2.00 PM 
            }
          ]}
          startAccessor="start"
          endAccessor="end"
          style={{ height: 500 }}
          onSelectSlot={this.handleSelect}
        />
      </div>
    )
  }
}


export default MyCalendar;
