    // });
    return (
        <div>
            <Card className='top-bar'>
                <CardBody>
                    <div className='d-flex justify-content-between'>
                        <h5>Employee directory</h5>
                        <span>
                            <input type='search' name='search' className='search' ></input>
                            <FontAwesomeIcon icon={faSearch} className='search-icon'></FontAwesomeIcon>
                        </span>
                    </div>
                </CardBody>
            </Card>
            <Card className='employee-directory'>
                <CardBody>
                    <Row className='justufy-content-between'>
                    <Col lg='3' key={persons.id}>
                <div className='employee-detail text-center '>

                    <CardBody className='text-center'>
        {data.url?<img src={process.env.PUBLIC_URL+ data.url} alt={persons.name} className='rounded-circle img-fluid'></img> : <p className='initials rounded-circle'><b>{initial}</b></p>}
                        <p className='my-1'>{persons.name}</p>
                        <p className='mb-0' style={{ fontSize: 14 }}>{persons.jobTitle}</p>
                    </CardBody>

                </div>
            </Col>
                    </Row>
                </CardBody>
            </Card>
        </div>
    )

}

export default Employee;
