import {
    CardBody,
    Card,
    Row,
    Col
} from 'reactstrap';
import axios from 'axios'
import './Employee.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch } from '@fortawesome/free-solid-svg-icons';


import React, { Component } from 'react'

export default class Employee extends Component {
    constructor(props) {
        super(props);
        this.state = {
            Users: [],
            nameInitials: [],
            searchItems:''
        };
    }

    handleSearch=(e)=>{
        this.setState({
            searchItems: e.target.value
        })
        const obj= employeee
        console.log(obj)
    }


    getUsersData() {

        axios
            .get('./employee_database.json')
            .then(res => {
                const data = res.data

                // ---------search------------



                // -------------Search finish----------------------
                const newData = data.filter((data)=>{
                    if(this.state.searchItems == null)
                        return data
                    else if(data.name.toLowerCase().includes(this.state.search.toLowerCase()) || data.jobTitle.toLowerCase().includes(this.state.search.toLowerCase())){
                        return data
                    }
                  }).map(data => {
                    let [firstname, lastname] = data.name.split(" ")
                    firstname = firstname.slice(0, 1)
                    lastname = lastname.slice(0, 1)
                    let initial = firstname + lastname,
                    initials = initial.slice(0,2)

                    return initial
                })
                this.setState({
                    Users: data,
                    nameInitials: newData
                })
            })
            .catch((error) => {
                console.log(error)
            })
    }
    componentDidMount() {
        this.getUsersData()
    }


    render() {
        return (
            <div>
                <Card className='top-bar'>
                    <CardBody>
                        <div className='d-flex justify-content-between'>
                            <h5>Employee directory</h5>
                            <span>
                                <input type='search' name='search' className='search' value={this.state.searchItems} onChange={this.handleSearch}></input>
                                <FontAwesomeIcon icon={faSearch} className='search-icon'></FontAwesomeIcon>
                            </span>
                        </div>
                    </CardBody>
                </Card>
                <Card className='employee-directory'>
                    <CardBody>
                        <Row className='justufy-content-between'>
                            {this.state.Users.map((person , i)=>

                                < Col lg='3' key={person.id} >
                                    <div className='employee-detail text-center '>

                                        <CardBody className='text-center'>
                                            {person.url ? <img src={process.env.PUBLIC_URL + person.url} alt={person.name} className='rounded-circle img-fluid'></img> : <p className='initials rounded-circle'><b>{this.state.nameInitials[i]}</b></p>}
                                            <p className='my-1'>{person.name}</p>
                                            <p className='mb-0' style={{ fontSize: 14 }}>{person.jobTitle}</p>
                                        </CardBody>

                                    </div>
                                </Col >
                            )}
                        </Row>
                    </CardBody>
                </Card>
            </div>
        )
    }
}
