import React, { useState } from 'react'
import { Calendar, Views, momentLocalizer } from 'react-big-calendar'
import moment from 'moment'
import events from './events'
import * as dates from '../node_modules/react-big-calendar/lib/utils/dates'
import 'react-big-calendar/lib/css/react-big-calendar.css'
import { Button, Modal, ModalHeader, ModalBody, ModalFooter, Form, FormGroup, Label, Input } from 'reactstrap';

let allViews = Object.keys(Views).map(k => Views[k])

const localizer = momentLocalizer(moment)

const ColoredDateCellWrapper = ({ children }) =>
  React.cloneElement(React.Children.only(children), {
    style: {
      backgroundColor: 'lightblue',
    },
  })


let MyCalendar = () => {
  // const [modal] = useState(true);
  const [event, setevent] = useState(events)
  const [modal, setModal] = useState(false);
  const toggle = () => setModal(!modal);
  const [input, setInput]=useState({eventName:'', eventStart:'', eventEnd:''})

  // const eventModal = () => {

  //   debugger
  //   return (alert('hello'))
  // }
  const handleChange=(e)=>{
    setInput({[e.target.name]:e.target.value})
    console.log(e.target.value)

  }
  const handleSelect = ({ start, end }) => {
    const title = window.prompt('New Event name')
    if (title)
      setevent({
        events: [
          ...this.state.events,
          {
            start,
            end,
            title,
          },
        ],
      })
  }
  return (
    <div>
      <Calendar
        events={event}
        views={allViews}
        step={60}
        showMultiDayTimes
        max={dates.add(dates.endOf(new Date(2020, 3, 1), 'day'), -1, 'hours')}
        defaultDate={new Date(2020, 3, 1)}
        components={{
          timeSlotWrapper: ColoredDateCellWrapper,
        }}
        localizer={localizer}
        style={{ height: 500 }}

        selectable
        onSelectEvent={event => alert(event.title)}
        onSelectSlot={handleSelect}
      />
      {/* {/* <Modal isOpen={modal} toggle={toggle} className="a">
