        <ModalBody>
          <Form>
            <FormGroup>
              <Label for="eventName">Name</Label>
              <Input type="text" name="eventName" id="eventName" value={input.eventName} onChange={handleChange}/>
            </FormGroup>
            <FormGroup>
              <Label for="eventStart">Start</Label>
              <Input type="date" name="eventStart" id="eventStart" value={input.eventStart} onchchange={handleChange}/>
            </FormGroup>
            <FormGroup>
              <Label for="eventEnd">End</Label>
              <Input type="date" name="eventEnd" id="eventEnd" />
            </FormGroup>
          </Form>
