export default class MyCalendar extends Component {
  state = {
    event: events,
    modal: false,
    eventName: '',
    eventStart: '',
    eventEnd: ''
  }
  toggle = () => this.setState({modal:(!this.state.modal)});

  handleChange = (e) => {
    this.setState({[e.target.name]: e.target.value })
    console.log(e.target.value)

  }
  handleSelect = ({ start, end }) => {
    this.toggle()
    // const title = window.prompt('New Event Name')

  }
  createEvent = ({ start, end }) => {
    debugger
    const title = this.state.eventName
    console.log(title)
    if (title != ' ' && title != null)
      setevent([
        ...event,
        {
          start: this.state.eventStart,
          end: this.state.eventEnd,
          title: title
        },
      ],
      )
    this.toggle()
  }
  render() {
    return (
      <div>
        <Calendar
          events={event}
          views={allViews}
          step={60}
          showMultiDayTimes
          max={dates.add(dates.endOf(new Date(2020, 3, 1), 'day'), -1, 'hours')}
          defaultDate={new Date(2020, 3, 1)}
          components={{
            timeSlotWrapper: ColoredDateCellWrapper,
          }}
          localizer={localizer}
          style={{ height: 500 }}

          selectable
          onSelectEvent={event => alert(event.title)}
          onSelectSlot={handleSelect}
        />
        <Modal isOpen={modal} toggle={toggle} className="a">
          <ModalHeader toggle={toggle}>Modal title</ModalHeader>
          <ModalBody>
            <Form>
              <FormGroup>
                <Label for="eventName">Name</Label>
                <Input type="text" name="eventName" id="eventName" value={input.eventName} onChange={handleChange} />
              </FormGroup>
              <FormGroup>
                <Label for="eventStart">Start</Label>
                <Input type="date" name="eventStart" id="eventStart" value={input.eventStart} onchchange={handleChange} />
              </FormGroup>
              <FormGroup>
                <Label for="eventEnd">End</Label>
                <Input type="date" name="eventEnd" id="eventEnd" />
              </FormGroup>
            </Form>
          </ModalBody>
          <ModalFooter>
            <Button color="primary" onClick={createEvent}>Do Something</Button>{' '}
            <Button color="secondary" onClick={toggle}>Cancel</Button>
          </ModalFooter>
        </Modal>
      </div>
    )
  }
}
