import {
    CardBody,
    Card,
    Row,
    Col
} from 'reactstrap';
import axios from 'axios'
import './Employee.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch } from '@fortawesome/free-solid-svg-icons';



import React, { Component } from 'react'

export default class Employee extends Component {
    constructor(props) {
        super(props);
        this.state = {
            Users: [],
            initials: '',
            firstname: '',
            lastname: '',
            nameInitials: []
        };
    }

    getUsersData() {

        axios
            .get('./employee_database.json')
            .then(res => {
                const data = res.data.map(employee => {
                    let [firstname, lastname] = data.name.split(" ")
                    firstname = firstname.slice(0, 1)
                    lastname = lastname.slice(0, 1)
                    let initial = firstname + lastname
                    console.log(initial)
                })

                console.log(this.state.nameInitials)

                this.setState({
                    Users: data,
                })

            })
            .catch((error) => {
                console.log(error)
            })

    }
    componentDidMount() {
        this.getUsersData()
    }


    render() {
        return (
            <div>
                <Card className='top-bar'>
                    <CardBody>
                        <div className='d-flex justify-content-between'>
                            <h5>Employee directory</h5>
                            <span>
                                <input type='search' name='search' className='search' ></input>
                                <FontAwesomeIcon icon={faSearch} className='search-icon'></FontAwesomeIcon>
                            </span>
                        </div>
                    </CardBody>
                </Card>
                <Card className='employee-directory'>
                    <CardBody>
                        <Row className='justufy-content-between'>
                            {this.state.Users.map(person =>

                                < Col lg='3' key={person.id} >
                                    <div className='employee-detail text-center '>

                                        <CardBody className='text-center'>
                                            {person.url ? <img src={process.env.PUBLIC_URL + person.url} alt={person.name} className='rounded-circle img-fluid'></img> : <p className='initials rounded-circle'><b>{this.state.initials}</b></p>}
                                            <p className='my-1'>{person.name}</p>
                                            <p className='mb-0' style={{ fontSize: 14 }}>{person.jobTitle}</p>
                                        </CardBody>

                                    </div>
                                </Col >
                            )}
                        </Row>
                    </CardBody>
                </Card>
            </div>
        )
    }
}

